using System;using System.Collections.Generic;using System.Linq;using System.Text;namespace LinkedList{    public class Node    {        public int data;        public Node next;    }    public class LinkedListOperation    {        public Node RrverseLinkedList(Node head)        {            Node next = null;            if(head==null) return head;            if (head.next == null) return head;            if(head.next.next==null)            {                next = head.next;                next.next = head;                head.next = null;                return next;            }            Node one = head;            next = head.next;            one.next = null;            while (next.next!=null)            {                var three = next.next;                next.next = one;                one = next;                next = three;            }            next.next = one;            return next;        }    }}